--
-- . Type VARCHAR2 doesn't exist; use VARCHAR.
-- . Primary keys should be defined as "BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY".
--

CREATE TABLE USER (ID BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, LOGIN VARCHAR(255) NOT NULL, EMAIL VARCHAR(255) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, STATUS SMALLINT NOT NULL, ACTIVATIONTOKEN VARCHAR(255) NULL);
CREATE TABLE USER_ROLES (USER_ID BIGINT NOT NULL, ELEMENT SMALLINT NOT NULL);
CREATE TABLE FOOD (ID BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, DTYPE VARCHAR(1) NOT NULL, NAME VARCHAR(255) NOT NULL, FOOD_GROUP SMALLINT);
CREATE TABLE FOOD_INGREDIENTS (FOOD_ID BIGINT NOT NULL, INGREDIENTS_ID BIGINT NOT NULL);
CREATE TABLE FOOD_NUTRIENTS (FOOD_ID BIGINT NOT NULL, ELEMENT SMALLINT NOT NULL);
CREATE TABLE MEAL (ID BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, INSTANT DATETIME NOT NULL);
CREATE TABLE PREPAREDFOOD (ID BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, FOOD_ID BIGINT NOT NULL, PREPARATIONMETHOD SMALLINT NOT NULL, FOREIGN KEY (FOOD_ID) REFERENCES FOOD(ID));
CREATE TABLE MEAL_DISHES (MEAL_ID BIGINT NOT NULL, DISH_ID BIGINT NOT NULL, PRIMARY KEY (MEAL_ID, DISH_ID), FOREIGN KEY (MEAL_ID) REFERENCES MEAL(ID), FOREIGN KEY (DISH_ID) REFERENCES PREPAREDFOOD(ID))







